mapper.insert:
insert into student(
name, no, gender, phone, id_card,
is_college, address, degree, graduation_date,
clazz_id
)
values (
#{name}, #{no}, #{gender}, #{phone}, #{idCard},
#{isCollege}, #{address}, #{degree}, #{graduationDate},
#{clazzId}
)

mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, degree = #{degree}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, violation_count = #{violationCount}, violation_score = #{violationScore},
where id = #{id}

mapper.select:
select s.*, c.name as clazzName from student s left join clazz c on s.clazz_id = c.id
and s.name like concat('%', #{name}, '%')
and s.degree = #{degree}
and s.clazz_id = #{clazzId}
order by s.no asc
